mapper.update:
update SYS_DATA_QUARTZ_JOBS t
t.BIZ_ID = #{bizId}, t.JOB_GROUP = #{group}, t.JOB_NAME = #{name}, t.JOB_INTRO = #{intro}, t.JOB_CRON = #{cron}, t.JOB_CLASS = #{clazz}, t.JOB_START = #{start}, t.JOB_DELAY = #{delay}, t.JOB_PERIOD = #{period}, t.JOB_STATUS = #{status}
where t.JOB_ID = #{id}

UPDATE SYS_DATA_QUARTZ_JOBS t
SET JOB_STATUS = #{status}
WHERE BIZ_ID = #{id}

mapper.select:
SELECT t.BIZ_ID, t.JOB_ID, t.JOB_GROUP, t.JOB_NAME, t.JOB_INTRO, t.JOB_CLASS, t.JOB_CRON, t.JOB_START, t.JOB_DELAY, t.JOB_PERIOD, t.JOB_STATUS, t.TIME24 FROM SYS_DATA_QUARTZ_JOBS t
AND t.BIZ_ID = #{model.bizId}
AND t.JOB_STATUS = #{model.status}
AND t.JOB_NAME like '%'|| #{model.name} ||'%'
